package com.example.movielist.data.mapper

import com.example.movielist.data.network.dto.TmdbMovieDto
import com.example.movielist.data.network.dto.TmdbMovieDetailResponse
import com.example.movielist.domain.model.Movie
import com.example.movielist.domain.model.MovieDetail
import javax.inject.Inject

class DtoMapper @Inject constructor() {

    fun toDomain(dto: TmdbMovieDto): Movie {
        return Movie(
            id = dto.id,
            title = dto.title,
            releaseDate = dto.releaseDate,
            posterPath = dto.posterPath,
            backdropPath = dto.backdropPath,
            voteAverage = dto.voteAverage
        )
    }

    fun toDomainDetail(dto: TmdbMovieDetailResponse): MovieDetail {
        return MovieDetail(
            id = dto.id,
            title = dto.title,
            overview = dto.overview,
            releaseDate = dto.releaseDate,
            runtime = dto.runtime,
            homepage = dto.homepage,
            posterPath = dto.posterPath,
            backdropPath = dto.backdropPath,
            voteAverage = dto.voteAverage
        )
    }
}

package com.example.movielist.data.mapper

import com.example.movielist.data.network.dto.TmdbMovieDto
import com.example.movielist.data.network.dto.TmdbMovieDetailResponse
import com.example.movielist.domain.model.Movie
import com.squareup.moshi.JsonAdapter
import com.squareup.moshi.Moshi
import com.squareup.moshi.Types
import javax.inject.Inject

class JsonMapper @Inject constructor(
    private val moshi: Moshi,
    private val dtoMapper: DtoMapper
) {
    private val movieDtoListAdapter: JsonAdapter<List<TmdbMovieDto>> = 
        moshi.adapter(Types.newParameterizedType(List::class.java, TmdbMovieDto::class.java))

    private val movieDetailAdapter: JsonAdapter<TmdbMovieDetailResponse> = 
        moshi.adapter(TmdbMovieDetailResponse::class.java)

    fun toJson(movieDtos: List<TmdbMovieDto>): String {
        return movieDtoListAdapter.toJson(movieDtos)
    }

    fun fromMoviesJson(json: String): List<Movie> {
        val dtos = movieDtoListAdapter.fromJson(json) ?: emptyList()
        return dtos.map { dtoMapper.toDomain(it) }
    }

    fun toJsonDetail(movieDetailDto: TmdbMovieDetailResponse): String {
        return movieDetailAdapter.toJson(movieDetailDto)
    }

    fun fromMovieDetailJson(json: String): com.example.movielist.domain.model.MovieDetail {
        val dto = movieDetailAdapter.fromJson(json) 
            ?: throw IllegalArgumentException("Invalid movie detail JSON")
        return dtoMapper.toDomainDetail(dto)
    }
}

package com.example.movielist.data.network

import com.example.movielist.BuildConfig
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.components.SingletonComponent
import okhttp3.Interceptor
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Retrofit
import retrofit2.converter.moshi.MoshiConverterFactory
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
object NetworkModule {
    @Provides
    fun provideAuthInterceptor(): Interceptor = Interceptor { chain ->
        val request = chain.request().newBuilder()
            .addHeader("Authorization", "Bearer ${BuildConfig.TMDB_BEARER}")
            .build()
        chain.proceed(request)
    }

    @Provides
    @Singleton
    fun provideOkHttp(authInterceptor: Interceptor): OkHttpClient {
        val logging = HttpLoggingInterceptor().apply { 
            level = HttpLoggingInterceptor.Level.BASIC 
        }
        return OkHttpClient.Builder()
            .addInterceptor(authInterceptor)
            .addInterceptor(logging)
            .build()
    }

    @Provides
    @Singleton
    fun provideRetrofit(client: OkHttpClient): Retrofit =
        Retrofit.Builder()
            .baseUrl("https://api.themoviedb.org/3/")
            .client(client)
            .addConverterFactory(MoshiConverterFactory.create())
            .build()

    @Provides
    @Singleton
    fun provideTmdbService(retrofit: Retrofit): TmdbService = retrofit.create(TmdbService::class.java)
}

package com.example.movielist.data.repository

import com.example.movielist.data.local.MovieDao
import com.example.movielist.data.local.entities.MovieDetailEntity
import com.example.movielist.data.local.entities.TrendingCacheEntity
import com.example.movielist.data.mapper.DtoMapper
import com.example.movielist.data.mapper.JsonMapper
import com.example.movielist.data.network.TmdbService
import com.example.movielist.domain.model.Movie
import com.example.movielist.domain.model.MovieDetail
import com.example.movielist.domain.repository.MovieRepository
import com.example.movielist.util.CacheValidator
import com.example.movielist.util.Logger
import javax.inject.Inject

class MovieRepositoryImpl @Inject constructor(
    private val tmdbService: TmdbService,
    private val dao: MovieDao,
    private val dtoMapper: DtoMapper,
    private val jsonMapper: JsonMapper,
    private val logger: Logger
) : MovieRepository {

    override suspend fun getTrendingMovies(page: Int): List<Movie> {
        val cached = dao.getTrendingCache(page)
        if (cached != null && CacheValidator.isValid(cached.lastFetched)) {
            logger.d("Repo", "Returning cached trending page=$page")
            return jsonMapper.fromMoviesJson(cached.moviesJson)
        }

        try {
            val response = tmdbService.getTrending(page)
            val movies = response.results.map { dtoMapper.toDomain(it) }
            dao.insertTrendingCache(
                TrendingCacheEntity(
                    page = page,
                    moviesJson = jsonMapper.toJson(response.results),
                    lastFetched = System.currentTimeMillis()
                )
            )
            logger.d("Repo", "Fetched trending from network and cached page=$page")
            return movies
        } catch (e: Exception) {
            if (cached != null) {
                logger.d("Repo", "Network failed, returning stale cache page=$page")
                return jsonMapper.fromMoviesJson(cached.moviesJson)
            }
            throw e
        }
    }

    override suspend fun searchMovies(query: String, page: Int): List<Movie> {
        val response = tmdbService.searchMovies(query, page)
        return response.results.map { dtoMapper.toDomain(it) }
    }

    override suspend fun getMovieDetail(id: Int): MovieDetail {
        val cached = dao.getMovieDetail(id)
        if (cached != null) {
            logger.d("Repo", "Returning cached detail id=$id")
            return jsonMapper.fromMovieDetailJson(cached.json)
        }
        val response = tmdbService.getMovieDetails(id)
        val domain = dtoMapper.toDomainDetail(response)
        dao.insertMovieDetail(
            MovieDetailEntity(
                id = id,
                json = jsonMapper.toJsonDetail(response),
                lastFetched = System.currentTimeMillis()
            )
        )
        logger.d("Repo", "Fetched detail from network and cached id=$id")
        return domain
    }
}

package com.example.movielist.di

import android.content.Context
import android.util.Log
import androidx.room.Room
import com.example.movielist.data.local.AppDatabase
import com.example.movielist.data.local.MovieDao
import com.example.movielist.data.repository.MovieRepositoryImpl
import com.example.movielist.domain.repository.MovieRepository
import com.example.movielist.util.Logger
import com.squareup.moshi.Moshi
import com.squareup.moshi.kotlin.reflect.KotlinJsonAdapterFactory
import dagger.Binds
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.android.qualifiers.ApplicationContext
import dagger.hilt.components.SingletonComponent
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
abstract class AppModule {

    @Binds
    abstract fun bindMovieRepository(impl: MovieRepositoryImpl): MovieRepository

    companion object {
        @Provides
        @Singleton
        fun provideMoshi(): Moshi = Moshi.Builder()
            .add(KotlinJsonAdapterFactory())
            .build()

        @Provides
        @Singleton
        fun provideAppDatabase(@ApplicationContext context: Context): AppDatabase =
            Room.databaseBuilder(
                context,
                AppDatabase::class.java,
                AppDatabase.DATABASE_NAME
            ).build()

        @Provides
        fun provideMovieDao(database: AppDatabase): MovieDao = database.movieDao()

        @Provides
        @Singleton
        fun provideLogger(): Logger = object : Logger {
            override fun d(tag: String, message: String) {
                Log.d(tag, message)
                println("[$tag] $message") // Also print to stdout for KMM compatibility
            }
        }
    }
}

